// NS definieren
var ns = "ns=39;";

// Datatype definieren
var dtBoolean = "datatype=Boolean"
var dtInt32 = "datatype=Int32"

// Variable definieren
var SignalColor = {};
SignalColor.topic = "i=6035;" + dtInt32
SignalColor.payload = 1 //red

var SignalOn = {};
SignalOn.topic = "i=6037;" + dtBoolean
SignalOn.payload = flow.get("DigitalInput.Lamp1")

var SignalMode = {};
SignalMode.topic = "i=6036;" + dtInt32
SignalMode.payload = 0

var messages = [SignalColor, SignalOn, SignalMode]

messages.forEach(function (msg) {
    msg.topic = ns + msg.topic;
    msg.payload = msg.payload;
    node.send(msg);
})

// value definieren
var DigitalInput_1 = msg.payload[0].value
var DigitalInput_2 = msg.payload[1].value
var DigitalInput_3 = msg.payload[2].value
var DigitalInput_4 = msg.payload[3].value
var DigitalInput_5 = msg.payload[4].value
var DigitalInput_6 = msg.payload[5].value
var DigitalInput_7 = msg.payload[6].value
var DigitalInput_8 = msg.payload[7].value

if (DigitalInput_1 == 0){ DigitalInput_1 = false}
else { DigitalInput_1 = true}

if (DigitalInput_2 == 0) { DigitalInput_2 = false }
else { DigitalInput_2 = true }

if (DigitalInput_3 == 0) { DigitalInput_3 = false }
else { DigitalInput_3 = true }

if (DigitalInput_4 == 1) { DigitalInput_4 = 0 }
else { DigitalInput_4 = 50 }

if (DigitalInput_5 == 1) { DigitalInput_5 = 100 }
else { DigitalInput_5 = 50 }

if (DigitalInput_7 == 0) { DigitalInput_7 = false }
else { DigitalInput_7 = true }

if (DigitalInput_8 == 0) { DigitalInput_8 = false }
else { DigitalInput_8 = true }

msg.topic = "DigitalInput"
msg.payload = {
    "Lamp1": DigitalInput_1,
    "Lamp2": DigitalInput_2,
    "Lamp3": DigitalInput_3,
    "Lamp4": DigitalInput_8,
    "Override_0": DigitalInput_4,
    "Override_100": DigitalInput_5,
    "EnergyOn": DigitalInput_6,
    "Spindle": DigitalInput_7    
}

flow.set(msg.topic, msg.payload);
return msg;

